'use client';
'use strict';

var react = require('@chakra-ui/react');
var NextImage = require('next/image');
var interop = require('./interop.cjs');

const ImageEl = interop.interopDefault(NextImage);
const imageProps = [
  "src",
  "alt",
  "sizes",
  "width",
  "height",
  "fill",
  "loader",
  "quality",
  "priority",
  "loading",
  "placeholder",
  "blurDataURL",
  "unoptimized",
  "onLoadingComplete",
  "alt",
  "crossOrigin",
  "decoding",
  "loading",
  "referrerPolicy",
  "sizes",
  "src",
  "useMap"
];
const Image = react.chakra(ImageEl, {
  shouldForwardProp: (prop) => imageProps.includes(prop)
});

exports.Image = Image;
